import React, { useState } from 'react';

function App() {
  const [fullName, setFullName] = useState('');
  const [title, setTitle] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [htmlContent, setHtmlContent] = useState('');

  const generateHTML = () => {
    const html = `
<table cellpadding="0" cellspacing="0" style="font-family: Arial, sans-serif;">
  <tr>
    <td style="padding-right: 20px; border-right: 2px solid #f9b233; text-align: center;">
      <img src="https://www.gracechristianschools.us/wp-content/uploads/2022/03/cropped-logo-199x199.png" alt="Grace Logo" style="width: 120px; margin-bottom: 10px;"><br>
    </td>
    <td style="padding-left: 20px;">
      <p style="margin: 0; font-size: 16px; font-weight: bold; color: #00468b;">${fullName}</p>
      <p style="margin: 0; font-size: 13px; color: #444;">${title}</p>
      <p style="margin: 8px 0 0 0; font-size: 13px; color: #444;">
        Grace All-Star Academy &nbsp;|&nbsp; Grace Christian School<br>
        Afterschool • Summer Camp • Private Education<br>
        10760 Thornmint Rd, San Diego, CA 92127
      </p>
      <p style="margin: 8px 0 0 0; font-size: 13px;">
        <strong>Phone:</strong> ${phone}<br>
        <strong>Email:</strong> <a href="mailto:${email}" style="color: #00468b;">${email}</a><br>
        <strong>Web:</strong> 
        <a href="https://www.gracechristianschools.us/" style="color: #00468b;">Grace Christian School</a> | 
        <a href="https://graceschool.ezjoin.events/" style="color: #00468b;">All-Star Academy</a>
      </p>
    </td>
  </tr>
</table>`;
    setHtmlContent(html);
  };

  const downloadFile = () => {
    const blob = new Blob([htmlContent], { type: 'text/html' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = `${fullName.replace(' ', '_')}_signature.html`;
    a.click();
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h1>Email Signature Generator</h1>
      <input placeholder="Full Name" value={fullName} onChange={e => setFullName(e.target.value)} /><br/><br/>
      <input placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} /><br/><br/>
      <input placeholder="Phone Number" value={phone} onChange={e => setPhone(e.target.value)} /><br/><br/>
      <input placeholder="Email Address" value={email} onChange={e => setEmail(e.target.value)} /><br/><br/>
      <button onClick={generateHTML}>Preview</button>
      <button onClick={downloadFile} style={{ marginLeft: '10px' }}>Download HTML</button>

      <div style={{ marginTop: '20px', border: '1px solid #ccc', padding: '20px' }}>
        <h3>Preview:</h3>
        <div dangerouslySetInnerHTML={{ __html: htmlContent }} />
      </div>
    </div>
  );
}

export default App;
